mapper.insert:
insert into movie (id, title, overview, posterpath, popularity, releasedate) values
( #{content.id}, #{content.title}, #{content.overview}, #{content.posterpath}, #{content.popularity}, #{content.releasedate} )
on duplicate key update id = id;

insert into tv (id, title, overview, posterpath, popularity, releasedate) values
( #{content.id}, #{content.title}, #{content.overview}, #{content.posterpath}, #{content.popularity}, #{content.releasedate} )
on duplicate key update id = id;

mapper.select:
select id, title, overview, posterpath, popularity, releasedate
from movie
where id = #{id}

select id, title, overview, posterpath, popularity, releasedate
from tv
where id = #{id}

select avg(rating) 
from netflixcomment
where seq = #{seq};
